$.validator.setDefaults({
    highlight: function(a) {
        $(a).closest(".form-group").removeClass("has-success").addClass("has-error")
    },
    success: function(a) {
        a.closest(".form-group").removeClass("has-error").addClass("has-success")
    },
    errorElement: "span",
    errorPlacement: function(a, b) {
        if (b.is(":radio") || b.is(":checkbox")) {
            a.appendTo(b.parent().parent().parent())
        } else {
            a.appendTo(b.parent())
        }
    },
    errorClass: "help-block m-b-none",
    validClass: "help-block m-b-none"
});
$().ready(function() {
    $("#commentForm").validate();
    var a = "<i class='fa fa-times-circle'></i> ";
    $("#signupForm").validate({
        rules: {
            firstname: "required",
            lastname: "required",
            username: {
                required: true,
                minlength: 2
            },
            password: {
                required: true,
                minlength: 5
            },
            confirm_password: {
                required: true,
                minlength: 5,
                equalTo: "#password"
            },
            email: {
                required: true,
                email: true
            },
            topic: {
                required: "#newsletter:checked",
                minlength: 2
            },
            agree: "required"
        },
        messages: {
            firstname: a + "请输入你的姓",
            lastname: a + "请输入您的名字",
            username: {
                required: a + "请输入您的用户名",
                minlength: a + "用户名必须两个字符以上"
            },
            password: {
                required: a + "请输入您的密码",
                minlength: a + "密码必须5个字符以上"
            },
            confirm_password: {
                required: a + "请再次输入密码",
                minlength: a + "密码必须5个字符以上",
                equalTo: a + "两次输入的密码不一致"
            },
            email: a + "请输入您的E-mail",
            agree: {
                required: a + "必须同意协议后才能注册",
                element: "#agree-error"
            }
        }
    });
    $("#username").focus(function() {
        var c = $("#firstname").val();
        var b = $("#lastname").val();
        if (c && b && !this.value) {
            this.value = c + "." + b
        }
    })
});